import React,{useEffect, useState} from "react";
import ReactDOM from "react-dom";
import Board from "@asseinfo/react-kanban";
import "@asseinfo/react-kanban/dist/styles.css";
import initial from './data.json'
//import "./style.css";

const data = data;


function UncontrolledBoard(props) {
  useEffect(() =>{
    console.log(board,'dsd');
  },[board])
  const {board} = props
  return (
    <Board
      allowRemoveLane
      allowRenameColumn
      allowRemoveCard
      onLaneRemove={console.log}
      onCardRemove={console.log}
      onLaneRename={console.log}
      initialBoard={board}
      allowAddCard={{ on: "top" }}
      onNewCardConfirm={draftCard => ({
        id: new Date().getTime(),
        ...draftCard
      })}
      onCardNew={console.log}
    />
  );
}

function App() {

  const [board, setBoard] = useState({
    columns: [
      {
        id: 1,
        title: "Design",
        cards: [
          {
            id: 1,
            title: "Card title 1",
            description: "Card content"
          },
          {
            id: 2,
            title: "Card title 2",
            description: "Card content"
          },
          {
            id: 3,
            title: "Card title 3",
            description: "Card content"
          }
        ]
      },
      {
        id: 2,
        title: "Devlopment",
        cards: [
          {
            id: 9,
            title: "Card title 4",
            description: "Card content"
          }
        ]
      },
      {
        id: 3,
        title: "Testing",
        cards: [
          {
            id: 10,
            title: "Card title 5",
            description: "Card content"
          },
          {
            id: 11,
            title: "Card title 6",
            description: "Card content"
          }
        ]
      },
      {
        id: 4,
        title: "Support",
        cards: [
          {
            id: 12,
            title: "Card title 7",
            description: "Card content"
          },
          {
            id: 13,
            title: "Card title 8",
            description: "Card content"
          }
        ]
      }
    ]
  });

  function Search(name){
    console.log("search function worked", name)
    let arrayFillter = data.columns.filter((keyword,i)=>{
    console.log( keyword.title.toLowerCase().startsWith(name.target.value.toLowerCase()));
    return keyword.title.toLowerCase().startsWith(name.target.value.toLowerCase()) 
   });

  let obj = {
    columns : arrayFillter
  }
  console.log(obj)
  setBoard({...obj})
  setTimeout(() => console.log("Borad value " , board),10000)
  }

  useEffect(() =>{
    console.log('use effect called...');
    // setBoard({columns:[...data.columns,,...board.columns]})
  },[board])

  return (

    <div>
      <input  
        className="Search" 
        type="text" 
        placeholder="Search......" 
        onChange={Search}/>
      <h4>Columns</h4>
      <Board
      allowRemoveLane
      allowRenameColumn
      allowRemoveCard
      onLaneRemove={console.log}
      onCardRemove={console.log}
      onLaneRename={console.log}
      children={board}
      allowAddCard={{ on: "top" }}
      onNewCardConfirm={draftCard => ({
        id: new Date().getTime(),
        ...draftCard
      })}
      onCardNew={console.log}
    >{board}</Board>
    </div>

  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
