import React,{useState} from "react";
import ReactDOM from "react-dom";
import Board from "@lourenci/react-kanban";
import "@lourenci/react-kanban/dist/styles.css";
// Use your own styles to override the default styles
import "./styles.css";

const board = {
  columns: [
    {
      id: 1,
      title: "Design",
      cards: [
        {
          id: 1,
          title: "Card title 1",
          description: "Card content"
        },
        {
          id: 2,
          title: "Card title 2",
          description: "Card content"
        },
        {
          id: 3,
          title: "Card title 3",
          description: "Card content"
        }
      ]
    },
    {
      id: 2,
      title: "Devlopment",
      cards: [
        {
          id: 9,
          title: "Card title 4",
          description: "Card content"
        }
      ]
    },
    {
      id: 3,
      title: "Testing",
      cards: [
        {
          id: 10,
          title: "Card title 5",
          description: "Card content"
        },
        {
          id: 11,
          title: "Card title 6",
          description: "Card content"
        }
      ]
    },
    {
      id: 4,
      title: "Support",
      cards: [
        {
          id: 12,
          title: "Card title 7",
          description: "Card content"
        },
        {
          id: 13,
          title: "Card title 8",
          description: "Card content"
        }
      ]
    }
  ]
};

function Search(){
  console.log("search function worked")
 let arrayFillter = board.columns.filter((keyword,i)=>{
  return keyword.title.toLowerCase().startsWith(keyword.title.toLowerCase()) 
 });
console.log(arrayFillter)
}
//setFilter(columns);


function UncontrolledBoard() {
  return (
    <Board
      allowRemoveLane
      allowRenameColumn
      allowRemoveCard
      onLaneRemove={console.log}
      onCardRemove={console.log}
      onLaneRename={console.log}
      initialBoard={board}
      allowAddCard={{ on: "top" }}
      onNewCardConfirm={draftCard => ({
        id: new Date().getTime(),
        ...draftCard
      })}
      onCardNew={console.log}
    />
  );
}

function App() {

  //const [filter,setFilter] = useState();

  return (

    <div>
      <input  
        className="Search" 
        type="text" 
        placeholder="Search......" 
        onChange={Search()}/>
      <h4>Columns</h4>
      <UncontrolledBoard />
    </div>

  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
